// фикс перемещения скролла к началу страницы при роутинге 


import { useEffect } from "react";
import { useLocation } from "react-router-dom";
export default function ScrollToTop() {
    const {pathname} = useLocation();

    useEffect(() => {
        window.scrollTo(0, 0);
    }, [pathname]);

    return null;
}
